mapper.select:
SELECT file.file_id AS a_file_id, file.folder_id AS a_folder_id, file.file_name AS a_file_name, file.s3_file_name AS a_s3_file_name, file.delete_flg AS a_delete_flg, file.s3_object_key AS a_s3_object_key, file.file_size AS a_file_size, file.full_text_type AS a_full_text_type, file.create_user AS a_create_user, file.create_date AS a_create_date, file.update_user AS a_update_user, file.update_date AS a_update_date, folder.folder_id AS b_folder_id, folder.parent_folder_id AS b_parent_folder_id, folder.folder_group AS b_folder_group, folder.folder_name AS b_folder_name, folder.s3_folder_name AS b_s3_folder_name, folder.developer_id AS b_developer_id, folder.mansion_id AS b_mansion_id, folder.s3_object_key AS b_s3_object_key, folder.delete_flg AS b_delete_flg, folder.private_flg AS b_private_flg, folder.explanatory_text AS b_explanatory_text, folder.create_user AS b_create_user, folder.create_date AS b_create_date, folder.update_user AS b_update_user, folder.update_date AS b_update_date FROM t_file AS file INNER JOIN t_folder AS folder ON file.folder_id = folder.folder_id WHERE file.delete_flg = false AND folder.delete_flg = false AND file.folder_id = #{folderId} AND file.file_name LIKE #{keyword}
AND folder.developer_id = #{developerId}
AND folder.mansion_id = #{mansionId}
AND (file.create_user = #{userId} OR folder.private_flg = false)
